mapper.select:
select * from base_role where PROJECT_CODE = #{PROJECT_CODE}

    and NAME LIKE CONCAT('%',#{NAME},'%')


    and STATE = #{STATE}

order by CREATE_DATE desc  limit #{start} , #{limit}

select count(*) from base_role where PROJECT_CODE = #{PROJECT_CODE}

    and NAME LIKE CONCAT('%',#{NAME},'%')


    and STATE = #{STATE}
