mapper.select:
select * from base_project


        (PROJECT_CODE LIKE CONCAT('%',#{NAME},'%') or PROJECT_NAME LIKE CONCAT('%',#{NAME},'%'))


        and STATUS = #{STATUS}


order by MODIFY_DATE desc  limit #{start} , #{limit}

select count(*) from base_project


        (PROJECT_CODE LIKE CONCAT('%',#{NAME},'%') or PROJECT_NAME LIKE CONCAT('%',#{NAME},'%'))


        and STATUS = #{STATUS}
